<!doctype html public "-//w3c//dtd html 4.0 transitional//en"><html><head>   <meta http-equiv="Content-Type" content="text/html; charset=iso-8859-1">   <meta name="Author" content="Ivan Dean">   <title>Processing Category Renames</title>  </head><body><h1>Processing Category Renames</h1><?phpinclude 'functions.php';/* Loop through each submitted variable, and if newcatnnnn is set,    update the category name for this item, including any database entries that    are currently in this category.*/while (list ($key, $val) = each ($_POST)) {     //echo "$key => $val<br />\n";     $index=substr($key,-3);     $oldcatstr="cat" . $index;     $newcatstr= "newcat" . $index;    if ($key == "cat$index") {    	$oldcat = $val;    	   	    	if (isset($_POST[$newcatstr]) && ($_POST[$newcatstr] != "")) {     	  echo "Updating name for $oldcat to $_POST[$newcatstr]<br>\n";    	      	  renamecat($oldcat,$_POST[$newcatstr]);    	  continue;    	}    }  } include 'footer.php';?></body></html>